function setCookie(e,o,n){var t=encodeURIComponent(e)+encodeURIComponent(o)+";path=/";if(isNaN(n)){var i=new Date;i.setDate(i.getDate()+n),t+=";expires="+n}document.cookie=t}function getCookie(e){for(var o=document.cookie.split(";"),n=0;n<o.length;n++){var t=o[n].split("=");if(encodeURIComponent(e)==t[0])return decodeURIComponent(t[1])}}function removeCookie(e){document.cookie=encodeURIComponent(e)+"=;expires="+new Date(0)+";path=/"}